import React, { useState, useEffect } from "react";
import { GoogleMap, InfoWindow, Marker } from "@react-google-maps/api";
// core components
import Header from "components/Headers/Header.js";
import { endpoints } from "../api-endpoints";

function Map() {
  const [activeMarker, setActiveMarker] = useState(null);
  // const [markers, setMarkers] = useState([]);
  // const [marker, setMarker] = useState();

  const handleActiveMarker = (drivers) => {
    if (drivers === activeMarker) {
      return;
    }
    setActiveMarker(drivers);
  };
  let drivers = [];

  useEffect(() => {
    async function fetchUsers() {
      const fullResponse = await fetch(endpoints.getDrivers);
      const responseJson = await fullResponse.json();
      let users = responseJson.users;
      users.forEach((user) => {
        drivers.push({
          id: user.address.lat,
          name: "Qevla Driver",
          position: user.address,
        });
      });
       console.log(
         "Created drivers>>>>>>> from headers from map markers used",
         drivers
       );
    }

    fetchUsers();
  }, []);

  const markers = [
    {
      id: 1,
      name: "Qevla Driver",
      position: { lat: 41.881832, lng: -87.623177 },
    },
    {
      id: 2,
      name: "Denver, Colorado",
      position: { lat: 39.739235, lng: -104.99025 },
    },
    {
      id: 3,
      name: "Los Angeles, California",
      position: { lat: 34.052235, lng: -118.243683 },
    },
    {
      id: 4,
      name: "New York, New York",
      position: { lat: 40.712776, lng: -74.005974 },
    },
  ];
  const google = window.google;
  const handleOnLoad = (map) => {
    const bounds = new google.maps.LatLngBounds();
    drivers.forEach(({ position }) => bounds.extend(position));
    map.fitBounds(bounds);
    // console.log("Created drivers>>>>>>> from headers from map markers", markers);
  };
  // console.log("Created drivers>>>>>>> from headers from map markers", markers);

  return (
    <>
      <Header />
      <GoogleMap
        onLoad={handleOnLoad}
        onClick={() => setActiveMarker(null)}
        mapContainerStyle={{ height: "600px" }}
      >
        {drivers.map(({ id, name, position }) => (
          <Marker
            key={id}
            position={position}
            onClick={() => handleActiveMarker(id)}
          >
            {activeMarker === id ? (
              <InfoWindow onCloseClick={() => setActiveMarker(null)}>
                <div>{name}</div>
              </InfoWindow>
            ) : null}
          </Marker>
        ))}
      </GoogleMap>
    </>
  );
}

export default Map;
