import React, { useEffect, useState } from "react";

// reactstrap components
import { Card, Container, Row } from "reactstrap";

// core components
import Header from "components/Headers/Header.js";
import { endpoints } from "../api-endpoints";

const MapWrapper = () => {
  const mapRef = React.useRef(null);
  const [drivers, setDrivers] = useState([]);
  // const [userLat, setLat] = useState([]);
  // const [userLng, setLng] = useState([]);
  const latRef = React.useRef(null);
  const lngRef = React.useRef(null);


  useEffect(() => {
    async function fetchUsers() {
      const fullResponse = await fetch(endpoints.getDrivers);
      const responseJson = await fullResponse.json();
      let users = responseJson.users;

      users.forEach((user) => {
        setDrivers(user.address);
      });
    }

    fetchUsers();
  }, []);
  console.log("Created drivers>>>>>>> from headers from map drivers", drivers);


  React.useEffect(() => {
  let google = window.google;
  let map = mapRef.current;
  let lat = latRef.current;
  let lng = lngRef.current;

  let latLng = new google.maps.LatLng(lat, lng);
      drivers.forEach((driver) => {
        lat = driver.lat;
        lng = driver.long;
        const mapOptions = {
          zoom: 12,
          center: latLng,
          scrollwheel: false,
          zoomControl: true,
          styles: [
            {
              featureType: "administrative",
              elementType: "labels.text.fill",
              stylers: [{ color: "#444444" }],
            },
            {
              featureType: "landscape",
              elementType: "all",
              stylers: [{ color: "#f2f2f2" }],
            },
            {
              featureType: "poi",
              elementType: "all",
              stylers: [{ visibility: "off" }],
            },
            {
              featureType: "road",
              elementType: "all",
              stylers: [{ saturation: -100 }, { lightness: 45 }],
            },
            {
              featureType: "road.highway",
              elementType: "all",
              stylers: [{ visibility: "simplified" }],
            },
            {
              featureType: "road.arterial",
              elementType: "labels.icon",
              stylers: [{ visibility: "off" }],
            },
            {
              featureType: "transit",
              elementType: "all",
              stylers: [{ visibility: "off" }],
            },
            {
              featureType: "water",
              elementType: "all",
              stylers: [{ color: "#5e72e4" }, { visibility: "on" }],
            },
          ],
        };

        map = new google.maps.Map(map, mapOptions);
        // var position = new google.maps.LatLng(lat, lng);
        const marker = new google.maps.Marker({
          position: latLng,
          map: map,
          animation: google.maps.Animation.DROP,
          title: "Qevla Driver",
        });

        const contentString =
          `<div class="info-window-content"><h2>${marker.title}</h2>` +
          "<p></p></div>";

        const infowindow = new google.maps.InfoWindow({
          content: contentString,
        });

        google.maps.event.addListener(marker, "click", function () {
          infowindow.open(map, marker);
        });
      });
  }, []);
  return (
    <>
      <div
        style={{ height: `600px` }}
        className="map-canvas"
        id="map-canvas"
        ref={mapRef}
      ></div>
    </>
  );
};

const Maps = () => {
  return (
    <>
      <Header />
      {/* Page content */}
      <Container className="mt--7" fluid>
        <Row>
          <div className="col">
            <Card className="shadow border-0">
              <h2 className="">Qevla Admin Map Monitor</h2>
              <MapWrapper />
            </Card>
          </div>
        </Row>
      </Container>
    </>
  );
};

export default Maps;
