import React, { useEffect, useState } from "react";

// reactstrap components
import { Card, Container, Row } from "reactstrap";

// core components
import Header from "components/Headers/Header.js";
import { endpoints } from "../api-endpoints";


const MapWrapper = () => {
  const mapRef = React.useRef(null);
  const [drivers, setDrivers] = useState([]);

  useEffect(() => {
    async function fetchUsers() {
      const fullResponse = await fetch(endpoints.getDrivers);
      const responseJson = await fullResponse.json();
      console.log(
        "Created drivers>>>>>>> from headers from map",
        responseJson.users
      );
      setDrivers(responseJson.users);
    }

    fetchUsers();
  }, []);
  React.useEffect(() => {

    let google = window.google;
    let map = mapRef.current;
    let lat = "6.469700";
    let lng = "3.705700";

    let latLng = new google.maps.LatLng(lat, lng);
    // const myLatlng = new google.maps.LatLng(lat, lng);
    const mapOptions = {
      zoom: 12,
      center: latLng,
      scrollwheel: false,
      zoomControl: true,
      styles: [
        {
          featureType: "administrative",
          elementType: "labels.text.fill",
          stylers: [{ color: "#444444" }],
        },
        {
          featureType: "landscape",
          elementType: "all",
          stylers: [{ color: "#f2f2f2" }],
        },
        {
          featureType: "poi",
          elementType: "all",
          stylers: [{ visibility: "off" }],
        },
        {
          featureType: "road",
          elementType: "all",
          stylers: [{ saturation: -100 }, { lightness: 45 }],
        },
        {
          featureType: "road.highway",
          elementType: "all",
          stylers: [{ visibility: "simplified" }],
        },
        {
          featureType: "road.arterial",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }],
        },
        {
          featureType: "transit",
          elementType: "all",
          stylers: [{ visibility: "off" }],
        },
        {
          featureType: "water",
          elementType: "all",
          stylers: [{ color: "#5e72e4" }, { visibility: "on" }],
        },
      ],
    };
var bounds = new google.maps.LatLngBounds();
  var markers = [
  ['Mumbai', 19.0760,72.8777],
  ['Pune', 18.5204,73.8567],
  ['Bhopal ', 23.2599,77.4126],
  ['Agra', 27.1767,78.0081],
  ['Delhi', 28.7041,77.1025],
];

    // {
    //   drivers.map((driver) => {
    //     //const onClick = props.onClick.bind(this, marker)
    //     return (
    //       <Marker
    //         key={marker.id}
    //         position={{ lat: marker.latitude, lng: marker.longitude }}
    //       >
    //         <InfoWindow>
    //           <div>{marker.shelter}</div>
    //         </InfoWindow>
    //       </Marker>
    //     );
    //   });
    // }
    map = new google.maps.Map(map, mapOptions);

for( i = 0; i < markers.length; i++ ) {
var position = new google.maps.LatLng(markers[i][1], markers[i][2]);
    bounds.extend(position);
    const marker = new google.maps.Marker({
      position: latLng,
      map: map,
      animation: google.maps.Animation.DROP,
      title: "Qevla INC",
    });
        // Each marker to have an info window    
    google.maps.event.addListener(marker, 'click', (function(marker, i) {
        return function() {
            infoWindow.setContent(infoWindowContent[i][0]);
            infoWindow.open(map, marker);
        }
    })(marker, i));
    // Automatically center the map fitting all markers on the screen
    map.fitBounds(bounds);
}


    const contentString =
      `<div class="info-window-content"><h2>${marker.title}</h2>` +
      "<p></p></div>";

    const infowindow = new google.maps.InfoWindow({
      content: contentString,
    });

    google.maps.event.addListener(marker, "click", function () {
      infowindow.open(map, marker);
    });
  }, []);
  return (
    <>
      <div
        style={{ height: `600px` }}
        className="map-canvas"
        id="map-canvas"
        ref={mapRef}
      ></div>
    </>
  );
};

const Maps = () => {
  return (
    <>
      <Header />
      {/* Page content */}
      <Container className="mt--7" fluid>
        <Row>
          <div className="col">
            <Card className="shadow border-0">
              <h2 className="">Qevla Admin Map Monitor</h2>
              <MapWrapper />
            </Card>
          </div>
        </Row>
      </Container>
    </>
  );
};

export default Maps;



